mapper.select:
	   select l.id, l.c_createDate, l.n_creater, l.c_loginIp,u.c_username
	   from loginrecord l,users u  
	   where 1=1
	    and l.n_creater = #{loginRecord.user.id}
   	    and c_loginIp = #{loginRecord.c_loginIp}
   	   	and substr(l.c_createDate,1,10) = #{loginRecord.c_createDate}
   	   order by u.id limit #{page.index},#{page.rows}

	   select count(id)
	   from loginrecord
	   where 1=1
	    and l.n_creater = #{loginRecord.user.id}
   	    and c_loginIp = #{loginRecord.c_loginIp}
   	   	and substr(l.c_createDate,1,10) = #{loginRecord.c_createDate}
   	   order by id limit #{page.index},#{page.rows}
