mapper.select:
	   select ,p.c_permisName,r.c_roleName,a.c_createDate,a.c_updateDate
	   from sys_role_permission a
	   left join sys_permission p
	   on p.id = a.n_permisId
	   left join sys_roles r
	   on r.id = a.n_roleId
	   where a.n_deleted = 0
	    and n_permisId = #{rp.n_permisId}
	    and n_roleId = #{rp.n_roleId}
	   	and substr(c_createDate,1,10) = #{rp.c_createDate}
   	     and substr(c_updateDate,1,10) = #{rp. c_updateDate}
   	   order by id limit #{page.index},#{page.rows}

	   select count(id)
	   from sys_role_permission
	   where 1=1
	    and n_permisId = #{rp.n_permisId}
	    and n_roleId = #{rp.n_roleId}
	   	and substr(c_createDate,1,10) = #{rp.c_createDate}
   	     and substr(c_updateDate,1,10) = #{rp. c_updateDate}
